import React from "react";
import {useParams} from "react-router-dom";
import axios from "axios";

export class HomePage extends React.Component {
    constructor(props) {
        super(props);
        // console.log("The Id is:" + this.useParams().id);
        this.state = {
            name: "",
            username: "",
        }
    }
    componentDidMount() {
        this.GetName();
    }

    GetName() {
        //axios.get("http://localhost:3001/user/" + this.props.match.params.id).then(res => {
        this.setState({name: this.props.match.params.id});
        axios.get("http://localhost:3001/user/name/" + this.state.username).then(res => {
            this.setState({
                name: res.data
            });
        }).catch(err => {
            console.log(err);
        });
    };


    render() {
        //this.GetName();
        console.log(this.state.name);
        return null;
    }
}
