import React, {Component} from "react";
import {Route, Routes, useLocation, useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import {cookies} from "../App";
import {Projects} from "./Projects";
import {MainPage} from "./MainPage";


export function Navbar(props) {
    return (
        <nav className="navbar navbar-dark navbar-expand-lg fixed-top bg-white portfolio-navbar gradient">
            <div className="container">
                <a className="navbar-brand logo" href="#">Brand</a>
                <button data-bs-toggle="collapse" className="navbar-toggler" data-bs-target="#navbarNav">
                    <span className="visually-hidden">Toggle navigation</span>
                    <span className="navbar-toggler-icon"></span></button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item"><a className="nav-link" href={"/"+props.username}>Home</a></li>
                        <li className="nav-item"><a className="nav-link active"
                                                    href={"/" + props.username + "/projects"}>Projects</a></li>
                        <li className="nav-item"><a className="nav-link" href={"/" + props.username + "/cv"}>CV</a></li>
                        <li className="nav-item"><a className="nav-link" href={"/" + props.username + "/hireMe"}>Hire me</a></li>

                    </ul>
                </div>
            </div>
        </nav>
    )

}

const withRouter = WrappedComponent => props => {
    // const params = useParams();
    // etc... other react-router-dom v6 hooks

    const location = useLocation();
    const navigate = useNavigate();
    const params = useParams();
    return <WrappedComponent {...props} location={location} navigate={navigate} params={params} />;
    // return (
    //     <WrappedComponent
    //         {...props}
    //         params={params}
    //         // etc...
    //     />
    // );
};


export class HomePage extends Component {
    logout = () => {
        cookies.remove("sessionToken");
        cookies.remove("username");
        axios.post("http://localhost:3001/user/logout", {token: cookies.get("sessionToken")}).then(res => {
            console.log(res.data);
            if (res.data === "success") {
                console.log("logout success");
                window.location.href = "/";
            }
        }).catch(err => {
            console.log(err);
        });
        //this.props.navigate("/login");
    }

constructor(props) {
    super(props);
    this.state = {
        name: "",
        username: this.props.id,
    }
    const sessionToken = cookies.get("sessionToken");
    if(sessionToken === undefined) {
        console.log("no session token");
        window.location.href = "/login";
    }

    console.log();
    axios.post("http://localhost:3001/user/auth", {token: sessionToken !== undefined? "" : sessionToken}).then(res => {
        console.log(res.data);

        // const navigateToLogin = () => {
        //     useNavigate("/login");
        // }

        if (res.data.msg === "success") {
            console.log("auth returned success");
        }
        if(res.data === "error") {
            window.location.href = "/login";
            //navigateToLogin();
        }
    }).catch(err => {
        console.log(err);
    });

    if(this.state.username === "") {
        window.location.reload();
    }
    //
    //
    // axios.get("http://localhost:3001/user/name/" + this.state.username).then(res => {
    //     this.state.name = res.data;
    //     document.getElementById("name").innerHTML = res.data + " is logged in";
    // }).catch(err => {
    //     console.log(err);
    // });

}

    componentDidMount() {
        //this.state.username = this.props.id;


    }



    render() {

        return (

            <div className="HomePage">

                <Navbar username={this.state.username}/>

                <Routes>
                    <Route path={"/projects"} element={<Projects />}/>
                    <Route path={"/"} element={<MainPage />}/>
                </Routes>


            </div>
        )


    }
}

export default withRouter(HomePage);
